package edu.kh.jvj.review.model.vo;

public class RvSearch {
	private int no; // 상품번호
	private int sr; // 검색조건
	private int cp;// 페이지
	public RvSearch() {
		// TODO Auto-generated constructor stub
	}
	public int getNo() {
		return no;
	}
	public void setNo(int no) {
		this.no = no;
	}
	public int getSr() {
		return sr;
	}
	public void setSr(int sr) {
		this.sr = sr;
	}
	public int getCp() {
		return cp;
	}
	public void setCp(int cp) {
		this.cp = cp;
	}
	
}
