mapper.insert:

 			SELECT SEQ_PRODUCT_NO.NEXTVAL FROM DUAL

		INSERT INTO PRODUCT VALUES(
		#{productNo} , #{title} , #{price} , DEFAULT , #{writecate}
		)

INSERT INTO NOTICE_BOARD VALUES(
	SEQ_NOTICE_NO.NEXTVAL , #{title}, #{editordata}, DEFAULT , #{loginMember} , #{noticecate}
)

mapper.update:
UPDATE NOTICE_BOARD SET
NOTICE_TITLE = #{title},
NOTICE_CONTENT =#{editordata},
NOTICE_CD =#{noticecate}
WHERE NOTICE_NO = #{noticeNo}

mapper.select:
SELECT MATCHRESULT.* , MR.MEMBER_NICKNAME, MR.MEMBER_NAME
FROM(
SELECT MEMBER_NO, COUNT(MEMBER_NO) MATCH_YN FROM MEMBER
WHERE MEMBER_EMAIL = #{adminId}
AND MEMBER_PW = #{adminPw}
AND STATUS_CD = 1
AND GRADE_CD = 101
GROUP BY MEMBER_NO
) MATCHRESULT
JOIN MEMBER MR ON (MATCHRESULT.MEMBER_NO= MR.MEMBER_NO)

SELECT COUNT(*) FROM NOTICE_BOARD
WHERE NOTICE_TITLE LIKE '%'||#{search}||'%' 

AND NOTICE_CD = #{cate}

SELECT NOTICE_NO , NOTICE_TITLE, NOTICE_CONTENT, TO_CHAR(NOTICE_CREATE_DT , 'YYYY-MM-DD') NOTICE_CREATE_DT, NOTICE_CD , NOTICE_TYPE_NM FROM NOTICE_BOARD JOIN NOTICE_CATEGORY USING (NOTICE_CD) WHERE NOTICE_TITLE LIKE '%'||#{search}||'%'
AND NOTICE_CD = #{cate}
ORDER BY NOTICE_CREATE_DT DESC

mapper.insert:
INSERT INTO PRODUCT VALUES(
SEQ_PRODUCT_NO.NEXTVAL , #{title} , #{price} , DEFAULT , #{writecate}
)

mapper.select:
SELECT COUNT(*) FROM PRODUCT
WHERE PRODUCT_NM LIKE '%'||#{search}||'%'

	AND PRODUCT_CD =#{cate}

SELECT PRODUCT_NO, PRODUCT_NM ,PRODUCT_PRICE, PRODUCT_CD, PRODUCT_CATE, TO_CHAR(CREATE_DT, 'YYYY-MM-DD') CREATE_DT FROM PRODUCT JOIN PRODUCT_CATEGORY USING(PRODUCT_CD) WHERE PRODUCT_NM LIKE '%'||#{search}||'%'
AND PRODUCT_CD =#{cate}
ORDER BY CREATE_DT DESC

mapper.update:
UPDATE PRODUCT SET
PRODUCT_NM = #{title} , 
PRODUCT_PRICE= #{price} 
WHERE PRODUCT_NO = #{productNo}

UPDATE STORE_DETAIL SET
STORE_EXP = #{editordata} ,
STORE_AMOUNT = #{stock} ,
STORE_MEMO = #{detailcontents} ,
STORE_TYPE = #{storecate}
WHERE PRODUCT_NO = #{productNo}

UPDATE DISCOUNT SET
DISCOUNT_START_DT = #{discountStart},
DISCOUNT_END_DT = #{discountEnd}, 
DISCOUNT_PER = #{discountPer}
WHERE PRODUCT_NO = #{productNo}

mapper.delete:
DELETE FROM PRODUCT_IMG 
WHERE PRODUCT_NO = #{productNo}
AND PRODUCT_IMG_LEVEL IN (${deleteLevels})
