INSERT INTO NOTICE_BOARD VALUES(
	SEQ_NOTICE_NO.NEXTVAL , #{title}, #{editordata}, DEFAULT , #{loginMember} , #{noticecate}
)

mapper.update:
UPDATE NOTICE_BOARD SET
NOTICE_TITLE = #{title},
NOTICE_CONTENT =#{editordata},
NOTICE_CD =#{noticecate}
WHERE NOTICE_NO = #{noticeNo}

mapper.select:
SELECT MATCHRESULT.* , MR.MEMBER_NICKNAME, MR.MEMBER_NAME
FROM(
SELECT MEMBER_NO, COUNT(MEMBER_NO) MATCH_YN FROM MEMBER
WHERE MEMBER_EMAIL = #{adminId}
AND MEMBER_PW = #{adminPw}
AND STATUS_CD = 1
AND GRADE_CD = 101
GROUP BY MEMBER_NO
) MATCHRESULT
JOIN MEMBER MR ON (MATCHRESULT.MEMBER_NO= MR.MEMBER_NO)

SELECT COUNT(*) FROM NOTICE_BOARD
WHERE NOTICE_TITLE LIKE '%'||#{search}||'%' 

AND NOTICE_CD = #{cate}

SELECT NOTICE_NO , NOTICE_TITLE, NOTICE_CONTENT, TO_CHAR(NOTICE_CREATE_DT , 'YYYY-MM-DD') NOTICE_CREATE_DT, NOTICE_CD , NOTICE_TYPE_NM FROM NOTICE_BOARD JOIN NOTICE_CATEGORY USING (NOTICE_CD) WHERE NOTICE_TITLE LIKE '%'||#{search}||'%'
AND NOTICE_CD = #{cate}
ORDER BY NOTICE_CREATE_DT DESC

mapper.insert:
INSERT INTO PRODUCT VALUES(
