SELECT (SELECT COUNT (*) FROM "LIKE" WHERE PRODUCT_NO = RSSET.PRODUCT_NO) LIKECOUNT, RSSET.* FROM( SELECT PRODUCT_NO, PRODUCT_NM, PRODUCT_PRICE, CLASS_MAX_PPL ,CLASS_EXP , TO_CHAR(CLASS_DT, 'YYYY-MM-DD') CLASS_DT , CLASS_TIME, CLASS_STATUS_CD, CLASS_STATUS_NM, PLACE_CODE, PLACE_NM, PRODUCT_IMG_PATH||PRODUCT_IMG_NAME PRODUCT_IMG_PATH FROM PRODUCT PRO JOIN CLASS_DETAIL CDE USING (PRODUCT_NO) JOIN CLASS_STATUS CST USING (CLASS_STATUS_CD) JOIN CLASS_PLACE CPL USING (PLACE_CODE) JOIN PRODUCT_IMG USING (PRODUCT_NO) WHERE CLASS_DT >= SYSDATE
AND TO_CHAR(CDE.CLASS_DT, 'YYYY-MM-DD') = #{selectdate}
AND PLACE_CODE = #{place}
AND TO_CHAR(CDE.CLASS_DT, 'YYYY-MM-DD') = #{selectdate} AND PLACE_CODE = #{place}
AND PRODUCT_IMG_LEVEL =0) RSSET ORDER BY CLASS_DT
