mapper.delete:
		DELETE FROM "LIKE"
		WHERE MEMBER_NO = #{memberNo}
        AND PRODUCT_NO = #{productNo}

choose.when:
UPDATE MEMBER SET 
MEMBER_PHONE = #{memberPhone}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_SERVICE = #{service}

UPDATE MEMBER SET 
MEMBER_NAME = #{memberName}, 
MEMBER_PHONE = #{memberPhone}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_SERVICE = #{service}

choose.otherwise:
UPDATE MEMBER SET 
MEMBER_NICKNAME = #{memberNickname}, 
MEMBER_PHONE = #{memberPhone}, 
MEMBER_ADDR = #{memberAddress} 
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER SET 
MEMBER_PW = #{modifyPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
STATUS_CD = 2
WHERE MEMBER_NO = #{memberNo}
