mapper.select:
SELECT A.PRODUCT_NO,A.PRODUCT_NM,A.PRODUCT_PRICE,B.STORE_AMOUNT,C.DISCOUNT_PER,B.STORE_TYPE
, (SELECT COUNT(*) FROM "LIKE" D WHERE MEMBER_NO = #{memberNo} AND D.PRODUCT_NO = A.PRODUCT_NO) LIKEIT
FROM PRODUCT A LEFT JOIN STORE_DETAIL B ON A.PRODUCT_NO = B.PRODUCT_NO LEFT JOIN DISCOUNT C ON A.PRODUCT_NO = C.PRODUCT_NO WHERE PRODUCT_CD= 1 AND A.PRODUCT_NO=#{no}

mapper.delete:
DELETE FROM "LIKE"
WHERE PRODUCT_NO = #{storeNo}
AND
MEMBER_NO =#{memberNo}
